CREATE TABLE `article` (
  `id` bigint(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(100) NOT NULL,
  `desc` varchar(250) NOT NULL DEFAULT '',
  `type` tinyint(3) unsigned NOT NULL,
  `img` varchar(150) NOT NULL DEFAULT '',
  `content` text NOT NULL,
  `status` tinyint(3) unsigned NOT NULL DEFAULT '1',
  `c_time` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00' ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  KEY `index_title` (`title`)
) ENGINE=InnoDB AUTO_INCREMENT=106 DEFAULT CHARSET=utf8;

CREATE TABLE `user` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'id 自增',
  `username` char(32) NOT NULL COMMENT '用户名 登录用',
  `nickname` char(32) DEFAULT NULL COMMENT '昵称,显示用',
  `password` char(32) NOT NULL COMMENT '密码',
  `email` char(64) DEFAULT NULL COMMENT '邮箱',
  `synopsis` text COMMENT '简介',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COMMENT='用户表';

CREATE TABLE `link` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `title` text NOT NULL COMMENT '标题',
  `link` text NOT NULL COMMENT '地址',
  `state` int(11) NOT NULL COMMENT '状态',
  `c_time` int(11) DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

CREATE TABLE `options` (
  `webname` text COMMENT '站点名称',
  `description` text COMMENT '站点描述',
  `keywords` text COMMENT '关键字'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


